import { signInWithEmailAndPassword } from "firebase/auth";
import React, { useContext, useState } from "react";
import { useNavigate } from "react-router-dom";
import { GlobalContext } from "../Context/GlobalContext";
import { auth } from "../firebase/firebase-config";

function LoginForm(props) {

const authorize=useContext(GlobalContext).authorize
authorize.email=localStorage.getItem('email');
console,log(authorize.email)
    
    return (
        <form action="" onClick={(e) => {
            e.preventDefault()

        }}>
            <div className="form">

                <h4>
                    Login
                </h4>

                <label>Your Email</label>
                <br />
                <input type="email" placeholder="enter your email" onChange={(e) => {
                    authorize?.chgLoginEmail(e.target.value)
                }} />
                <label>Your Password</label>
                <br />
                <input type="password" placeholder="enter your pass" autoComplete="on" onChange={(e) => {
                    authorize?.chgLoginPass(e.target.value)
                }} />
                <br /><br />
                <button onClick={authorize?.signin}> Login</button>
                <br />
                <p onClick={() => {
                    props.chgForm()
                }}>Create new account</p>

            </div>
        </form>

    );
}
export default LoginForm
