import React, { useContext } from "react";
import { Outlet, useNavigate } from "react-router-dom";
import { createUserWithEmailAndPassword,signOut } from "firebase/auth";
import { auth } from "../firebase/firebase-config";
import Navbar from "../Navbar/Navbar";
import { GlobalContext } from "../Context/GlobalContext";
import { useState } from "react";
import { signInWithEmailAndPassword } from "firebase/auth";

function Layout() {
    const [loginEmail, setLoginEmail] = useState("")
    const [loginPass, setLoginPass] = useState("")
    const [regEmail,setRegEmail] = useState("")
    const [regPass, setRegPass] = useState("")
    const storedUser = localStorage.getItem('email')
    const [user, setUser] = useState(storedUser);
const navigate=useNavigate();
function chgRegEmail(e){
    setRegPass(e.targe.value)
}

    const authorize = {
        user,
        loginEmail,
        loginPass,
        regEmail,
        regPass,
    


        signin: async () => {

            try {
                const user = await signInWithEmailAndPassword(auth, loginEmail, loginPass)
                const email = user?.user?.email;
                navigate("/welcome");
                localStorage.setItem('email', email);


            }
            catch (error) {
                console.log(error.message)
            }

        },
        signout: async () => {
            await signOut(auth);
        },
        register: async () => {
            try {
                const user = await createUserWithEmailAndPassword(auth, regEmail, regPass)
                console.log(user)
            }
            catch (error) {
                console.log(error.message)
            }

        }





    }


    return (
        <GlobalContext.Provider value={auth} >
            <Navbar />

            <Outlet></Outlet>

        </GlobalContext.Provider >

    )
}

export default Layout
